frappe.ui.form.on('Laboratory Report Item', {
  result: function (frm, cdt, cdn) {
    const row = locals[cdt][cdn];

    console.log("Row Data:", row); // ðŸ” Debug log

    if (!row.reference_range || !row.result) {
      frappe.model.set_value(cdt, cdn, 'status', '');
      return;
    }

    // Normalize dashes and split
    const cleanRange = row.reference_range.replace(/[â€“â€”âˆ’]/g, '-');
    const [minStr, maxStr] = cleanRange.split('-').map(val => val.trim());

    const min = parseFloat(minStr);
    const max = parseFloat(maxStr);
    const result = parseFloat(row.result);

    console.log("Parsed:", { min, max, result });

    if (!isNaN(min) && !isNaN(max) && !isNaN(result)) {
      const status = result >= min && result <= max ? "Normal" : "Abnormal";
      frappe.model.set_value(cdt, cdn, "status", status);
    } else {
      frappe.model.set_value(cdt, cdn, "status", '');
    }
  }
});

frappe.ui.form.on('Billing', {
    refresh: function(frm) {

             frm.add_custom_button('Create Laboratory Report', function() {
                frappe.call({
                    method: "frappe.client.insert",
                    args: {
                        doc: {
                            doctype: "Laboratory Report",
                            billing: frm.doc.name,
                            health_assessment_center:frm.doc.health_assessment_center,
                            patient_name: frm.doc.patient,
                            age:frm.doc.age,
                            contact_number:frm.doc.contact_number,
                            gender:frm.doc.gender,
                            sample: frm.doc.sample_type.map(sample_item => ({
                                sample_type: sample_item.sample_name,
                                doctype: "Laboratory Report Item"
                            }))
                        }
                    },
                    callback: function(response) {
                        if (response.message) {
                            frappe.set_route("Form", "Laboratory Report", response.message.name);
                        }
                    }
                });
            });

    }
});

frappe.ui.form.on('Laboratory Report', {
    refresh: function(frm) {
        frm.add_custom_button(__('Fetch Components'), function() {
            fetch_and_fill_components(frm);
        });
    }
});

async function fetch_and_fill_components(frm) {
    try {
        frappe.show_alert(__('Fetching lab data...'), 5);
        frm.dashboard.show_progress('Fetching data', 100, 'blue', true);

        const response = await frappe.call({
            method: 'codesphereit.api.lab_report.get_test_name_data'
        });

        const lab_data = response.message;
        if (!lab_data || lab_data.length === 0) {
            frappe.throw(__('No test data returned from server.'));
            return;
        }

        // Create a lookup of test components by health_assessment_name
        const test_map = {};
        lab_data.forEach(test => {
            if (test.health_assessment_name) {
                test_map[test.health_assessment_name.trim()] = test.test_components || [];
            }
        });

        const original_rows = frm.doc.sample.slice(); // clone to preserve order
        const reordered_rows = [];

        original_rows.forEach(group_row => {
            const sample_type = group_row.sample_type?.trim();
            group_row.is_group = 1; // Mark as group row
            reordered_rows.push(group_row);

            if (!sample_type || !test_map[sample_type]) return;

            const components = test_map[sample_type];

            components.forEach(comp => {
                const new_row = frappe.model.get_new_doc('Laboratory Report Item', frm.doc, 'sample');
                new_row.sample_type = '';
                new_row.component = comp.test_component_name;
                new_row.reference_range = comp.normal_range;
                new_row.unit = comp.unit;
                new_row.is_group = 0; // Mark component rows as not group

                reordered_rows.push(new_row);
            });
        });

        // Set the sample child table with reordered rows
        frm.doc.sample = reordered_rows;

        // Manually reset idx to preserve order
        frm.doc.sample.forEach((row, index) => {
            row.idx = index + 1;
        });

        frm.refresh_field('sample');
        frappe.show_alert(__('Components added successfully'), 5);

    } catch (error) {
        console.error('Error:', error);
        frappe.msgprint({
            title: __('Error'),
            message: __('Failed to update components: {0}', [error.message]),
            indicator: 'red'
        });
    } finally {
        frm.dashboard.hide_progress();
    }
}

frappe.ui.form.on('Lap Item', {
    rate: update_total_amount,
    range: update_total_amount,
    sample_type_remove: update_total_amount,
});

function update_total_amount(frm) {
    let total = 0;
    (frm.doc.sample_type || []).forEach(row => {
        total += (parseFloat(row.rate) || 0); // Or use row.rate * row.range
    });
    frm.set_value('total_amount', total);
}
